import {createStore} from 'redux'

const initialState = { 
    email: '',
    password: '',
};

const crudReducer = (state = initialState,action) => {
    if(action.type == "email"){
        return{
            ...state,
            email:action.value
        }
    }
    if(action.type == "password"){
        return{
            ...state,
            password:action.value
        }
    }
    return state;
}

const crudStore = createStore(crudReducer)

export default crudStore;
